package org.reactnative.camera.events;

import androidx.core.util.Pools;
import com.facebook.react.bridge.Arguments;
import com.facebook.react.bridge.WritableMap;
import com.facebook.react.uimanager.events.Event;
import com.facebook.react.uimanager.events.RCTEventEmitter;
import org.reactnative.camera.CameraViewManager;
import org.iopan.motiondetector.MotionDetector;

public class MotionDetectionErrorEvent extends Event<MotionDetectionErrorEvent> {
  private static final Pools.SynchronizedPool<MotionDetectionErrorEvent> EVENTS_POOL = new Pools.SynchronizedPool<>(3);
  private MotionDetector mFaceDetector;

  private MotionDetectionErrorEvent() {
  }

  public static MotionDetectionErrorEvent obtain(int viewTag, MotionDetector faceDetector) {
    MotionDetectionErrorEvent event = EVENTS_POOL.acquire();
    if (event == null) {
      event = new MotionDetectionErrorEvent();
    }
    event.init(viewTag, faceDetector);
    return event;
  }

  private void init(int viewTag, MotionDetector faceDetector) {
    super.init(viewTag);
    mFaceDetector = faceDetector;
  }

  @Override
  public short getCoalescingKey() {
    return 0;
  }

  @Override
  public String getEventName() {
    return CameraViewManager.Events.EVENT_ON_MOTION_DETECTION_ERROR.toString();
  }

  @Override
  public void dispatch(RCTEventEmitter rctEventEmitter) {
    rctEventEmitter.receiveEvent(getViewTag(), getEventName(), serializeEventData());
  }

  private WritableMap serializeEventData() {
    WritableMap map = Arguments.createMap();
    map.putBoolean("isOperational", mFaceDetector != null && mFaceDetector.isOperational());
    return map;
  }
}
